        public string SolvePart1()
        {
            /* Old version
            int up = input.Count(x => x == '(');
            int down = input.Count(x => x == ')');
            Console.WriteLine("1.1: {0}", up - down);*/

            // First half
            return input.Sum(x => x == '(' ? 1 : -1).ToString();
        }

        public string SolvePart2()
        {
            int floor = 0;
            /* Old version
            for (int i = 0; i < input.Length; i++)
            {
                floor += input[i] == '(' ? 1 : -1;

                if (floor == -1)
                {
                    Console.WriteLine("1.2: {0}", i + 1);
                    break;
                }
            }*/

            return (input.Select((x, i) =>
            {
                floor += x == '(' ? 1 : -1;
                return new { floor, i };
            }).FirstOrDefault(x => x.floor == -1).i + 1).ToString();
        }
